import React, { Component, useState } from 'react';
import { useHistory } from "react-router-dom";
import { Pedidos } from "./Pedidos"
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


const CargarPedido = () => {
    
    const [pedido, setPedido] = useState ({
        id: '',
        fecha: '',
        cantidad: '',
        nombreCliente: '',
        importe: ''
    });

    const {cargarPedido} = Pedidos;


    const handleDatos = (event, pedido) => {

        this.setPedido({
            [event.target.fecha]: event.target.value
        })

        pedidos.push(pedido);
    }

    const handleForm = (event) => {
        cargarPedido(this.pedido);
        
        event.stopPropagation();
        event.preventDefault();
    }

    return (
        <div className="form">
            <Form onSubmit={handleForm}>
                <h2>Cargar nuevo pedido</h2>
                <div className="d-flex justify-content-center">
                    <div className="p-3 mr-5 ml-5 container">
                        <Form.Group controlId="nroOrden">
                            <Form.Control type="number" disabled="true" name="distance" value={pedido.nroOrden}/>
                        </Form.Group>
                        <Form.Group controlId="fechaEntrega">
                            <Form.Control type="date" placeholder="Fecha de Entrega" name="date" onChange={handleDatos} value={this.pedido.fecha}/>
                        </Form.Group>
                        <Form.Group controlId="cantidad">
                            <Form.Control type="number" placeholder="Cantidad" name="date" onChange={handleDatos} value={this.pedido.cantidad}/>
                        </Form.Group>
                        <Form.Group controlId="cliente">
                            <Form.Control type="text" placeholder="Nombre del Cliente" name="date" onChange={handleDatos} value={this.pedido.nombreCliente}/>
                        </Form.Group>
                        <Form.Group controlId="importe">
                            <Form.Control type="text" placeholder="Importe" name="date" onChange={handleDatos} value={this.pedido.importe}/>
                        </Form.Group>
                    </div>
                </div>
                <Button className="mt-5 mb-5 mr-2 ml-2" size="lg" variant="info" type="submit">Cargar pedido</Button>
                <Button className="mt-5 mb-5 mr-2 ml-2" size="lg" variant="info" to="/qChurreria"> Cancelar </Button>
            </Form>
        </div>
    )

}

export default CargarPedido;
