import { Component, useState } from "react";

const Pedidos = () => {

    const [arrayPedidos, setArrayPedidos] = useState(['']);

    const cargarPedido = (pedido) => {
        arrayPedidos.push(pedido);
        return arrayPedidos;
    }

}

export default this.cargarPedido;
