import React, { Component, useState } from 'react';
import Pedidos from "./Pedidos"
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


class CargarPedido extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id: '',
            fecha: '',
            cantidad: '',
            nombreCliente: '',
            importe: ''
        }
    }
    

    handleDatos = (event) => {

        this.setPedido({
            [event.target.fecha]: event.target.value
        })
    };

    handleForm = (event) => {
        Pedidos.cargarPedido(this.pedido);
        
        event.stopPropagation();
        event.preventDefault();
    };

    render () {
        return (
            <div className="form">
                <Form onSubmit={handleForm}>
                    <h2>Cargar nuevo pedido</h2>
                    <div className="d-flex justify-content-center">
                        <div className="p-3 mr-5 ml-5 container">
                            <Form.Group>
                                <Form.Control type="number" disabled="true" name="distance" value={this.state.id}/>
                            </Form.Group>
                            <Form.Group>
                                <Form.Control type="date" placeholder="Fecha de Entrega" name="date" onChange={handleDatos} value={this.state.fecha}/>
                            </Form.Group>
                            <Form.Group>
                                <Form.Control type="number" placeholder="Cantidad" name="date" onChange={handleDatos} value={this.state.cantidad}/>
                            </Form.Group>
                            <Form.Group>
                                <Form.Control type="text" placeholder="Nombre del Cliente" name="date" onChange={handleDatos} value={this.state.nombreCliente}/>
                            </Form.Group>
                            <Form.Group>
                                <Form.Control type="text" placeholder="Importe" name="date" onChange={handleDatos} value={this.state.importe}/>
                            </Form.Group>
                        </div>
                    </div>
                    <Button className="mt-5 mb-5 mr-2 ml-2" size="lg" variant="info" type="submit">Cargar pedido</Button>
                    <Button className="mt-5 mb-5 mr-2 ml-2" size="lg" variant="info" to="/qChurreria"> Cancelar </Button>
                </Form>
            </div>
        )
    }
}

export default CargarPedido;
