const { React, Component, useState } = require('react');
const { CargarNuevoPedido } = require('./Pedidos');
const { Form, Button } = require('react-bootstrap');

class CargarPedido extends Component {

    constructor(props) {
        super(props);

        this.redireccionar = this.redireccionar.bind(this);

        this.pedido = {
            id: 123,
            fechaetrega: "",
            cantidad: "",
            cliente: "",
            importe: ""
        }
        
    }
    
    //let history = useHistory();

    handleDatos = (event) => {

        this.setState({
            value: event.target.value
        })
        console.log(event.target.value);

    };

    handleForm = (event) => {
        CargarNuevoPedido(this.pedido);
        
        event.stopPropagation();
        event.preventDefault();
    };

    redireccionar = () => {
        this.props.history.push('/cargarPedido');
    }

    render () {
        return (
            <div className="form">
                <Form onSubmit={this.handleForm}>
                    <h2>Cargar nuevo pedido</h2>
                    <div className=" justify-content-center">
                        <div className="p-3 mr-5 ml-5 container">
                            <input disabled={true} type= "text" name="distance" value={this.pedido.id + 1 }/>
                        </div>
                        <div className="p-3 mr-5 ml-5 container">
                            <input type="date" placeholder="Fecha de Entrega" name="date" onChange={this.handleDatos}/>
                        </div>
                        <div className="p-3 mr-5 ml-5 container">
                            <input type="number" placeholder="Cantidad" name="cantidad" onChange={this.handleDatos}/>
                        </div>
                        <div className="p-3 mr-5 ml-5 container">
                            <input type="text" placeholder="Nombre del Cliente" name="cliente" onChange={this.handleDatos}/>
                        </div>
                        <div className="p-3 mr-5 ml-5 container">
                            <input type="text" placeholder="Importe" name="importe" onChange={this.handleDatos}/>
                        </div>
                    </div>
                    <Button className="mt-5 mb-5 mr-2 ml-2" size="lg" variant="info" type="submit">Cargar pedido</Button>
                    <Button className="mt-5 mb-5 mr-2 ml-2" size="lg" variant="info" onClick={this.redireccionar}> Cancelar </Button>
                </Form>
            </div>
        )
    }
}

export default CargarPedido;
